mapper.select:
SELECT COUNT(*)
FROM content_view_log
WHERE member_id = #{memberId}

SELECT COUNT(*)
FROM content_view_log
WHERE member_id = #{memberId}
  AND content_type = #{type}

SELECT COUNT(*)
FROM content_view_log
WHERE member_id = #{memberId}
  AND viewed_at BETWEEN #{start} AND #{end}

SELECT EXISTS (
    SELECT 1
    FROM content_view_log
    WHERE member_id = #{memberId}
      AND view_date >= (CURDATE() - INTERVAL 30 DAY)
)
