mapper.select:
SELECT
    id, dcls_month, fin_co_no, fin_prdt_cd, mrtg_type, mrtg_type_nm,
    rpay_type, rpay_type_nm, lend_rate_type, lend_rate_type_nm,
    lend_rate_min, lend_rate_max, lend_rate_avg
FROM mortgage_loan_option
WHERE dcls_month = #{dclsMonth}
  AND fin_co_no = #{finCoNo}
  AND fin_prdt_cd = #{finPrdtCd}
