mapper.insert:
INSERT INTO user_investment_constraints (
    user_id,
    constraint_type,
    is_active
) VALUES (
    #{userId},
    #{constraintType},
    #{isActive}
)

mapper.update:
UPDATE user_investment_constraints
SET is_active = false
WHERE user_id = #{userId}

UPDATE user_investment_constraints
SET is_active = true
WHERE user_id = #{userId} AND constraint_type = #{constraintType}
